css:
.print-format {
    padding: 0.30in !important;
    font-size: 14px !important;
    font-family: "Times New Roman", Times, serif !important;
}
table {
    border: 1px solid black;
    border-collapse: collapse;
    width: 100%;
    table-layout: fixed;
}

td, th {
    border: 1px solid black;
    padding: 5px;
    text-align: left;
    box-sizing: border-box;
}

td:nth-child(1) {
    width: 20%; 
}

td:nth-child(2) {
    width: 40%; 
}

td:nth-child(3) {
    width: 40%; 
}

.amount{
    line-height: 1.5;
    font-size: 13px;
}
.split-content {
    width: 100% !important;
    line-height: 1.5;
}

.left-part, .right-part {
    width: 48% !important;
    display: inline-block;
    vertical-align: top;
}

.first-part, .second-part, .third-part {
    display: inline-block;
    vertical-align: top;
}

.first-part {
    width: 55% !important;
    text-align: left;
}

.second-part, .third-part {
    width: 20% !important;
    text-align: right;
}
center{
    font-size:16px !important;
}

.firstpart {
    width: 70% !important;
    display: inline-block;
}

.secondpart {
    width: 28% !important;
    display: inline-block;
    text-align: right;
}

footer {
    text-align: center;
    font-size: 10px;
}
#header-html{
    padding:5px !important;
}

@media print {
    .print-format {
        padding: 0 !important;
        font-family: "Times New Roman", Times, serif !important; 
    }
}
.print-heading {
    border-bottom: 0px solid #d1d8dd;
    padding:0px !important;
    margin-bottom: 0px !important;
    margin-top: 0px !important;
}

html:
{% set emp_doc = frappe.get_doc("Employee", doc.employee) %}
<table>
    <tr>
        <td colspan="2">
            <p id="header-html">
                {% if letter_head and not no_letterhead -%}
                {% if print_settings.repeat_header_footer %}
                   <div class="letter-head print-heading">{{ letter_head }}</div>
                {%- endif %}{%- endif %}
            </p>
            <p style="font-size: 13px;"><center><b>Payslip for the month of 
                {% if frappe.utils.formatdate(doc.start_date, 'MMMM yyyy') == frappe.utils.formatdate(doc.end_date, 'MMMM yyyy') %}
                    {{ frappe.utils.formatdate(doc.start_date, 'MMMM yyyy') }}
                {% else %}
                    {{ frappe.utils.formatdate(doc.start_date, 'MMMM yyyy') }} to {{ frappe.utils.formatdate(doc.end_date, 'MMMM yyyy') }}
                {% endif %}
            </b></center></p>

        </td>
    </tr>
    <tr>
        <td>
            <div class="split-content">
                <div class="left-part">
                    <p>Name:</p>
                    <p>Joining Date:</p>
                    <p>Designation:</p>
                    <p>Department:</p>
                    <p>Location:</p>
                    <p>Effective Work Days:</p>
                    <p>LOP:</p>
                </div>
                <div class="right-part">
                    <!-- {% if doc.employee_name %}
                        <p>{{ doc.employee_name }}</p>
                    {% endif %}
                    {% if frappe.get_doc('Employee', doc.employee).date_of_joining %}
                        <p>{{ frappe.utils.formatdate(frappe.get_doc('Employee', doc.employee).date_of_joining, 'dd-mm-yyyy') }}</p>
                    {% endif %} -->
                    <p>{{ doc.employee_name or "&nbsp;" }}</p>
                    <p>{{ frappe.utils.formatdate(frappe.get_doc('Employee', doc.employee).date_of_joining, 'dd-mm-yyyy') or "&nbsp;" }}</p>
                    <p>{{ frappe.get_doc('Employee', doc.employee).designation or "&nbsp;" }}</p>
                    <p>{{ frappe.get_doc('Employee', doc.employee).department or "&nbsp;" }}</p>
                    <p>{{ frappe.get_doc('Employee', doc.employee).branch or "&nbsp;" }}</p>
                    <p> {% if doc.total_working_days == doc.total_working_days | int %}
                            {{ doc.total_working_days | int or "&nbsp;"}}
                        {% else %}
                            {{ doc.total_working_days or "&nbsp;" }}
                        {% endif %}
                    </p>
                    <p>{% if doc.leave_without_pay == doc.leave_without_pay | int %}
                            {{ doc.leave_without_pay | int or "&nbsp;"}}
                        {% else %}
                            {{ doc.leave_without_pay or "&nbsp;" }}
                        {% endif %}
                    </p>
                </div>
            </div>
        </td>
        <td>
            <div class="split-content">
                <div class="split-content">
                    <div class="left-part">
                        <p>Employee No:</p>
                        <p>Bank Name:</p>
                        <p>Bank Account No:</p>
                        <p>PAN Number:</p>
                        <p>PF No:</p>
                        <p>PF UAN:</p>
                        <p>ESIC Number:</p>
                    </div>
                    <div class="right-part">
                        <p>{{doc.employee or "&nbsp;" }}</p>
                        <p>{{doc.bank_name or "&nbsp;" }}</p>
                        <p>{{doc.bank_account_no or "&nbsp;" }}</p>
                        <p>{{ frappe.get_doc('Employee', doc.employee).pan_number or "&nbsp;" }}</p>

                        {% if emp_doc.custom_pf_consent == "Yes" %}
                            <p>{{ emp_doc.provident_fund_account or "" }}</p>
                            <p>{{ emp_doc.custom_uan_number or "" }}</p>
                        {% else %}
                            <p>Not Applicable</p>
                            <p>Not Applicable</p>
                        {% endif %}

                        {% if emp_doc.custom_esi_consent == "Yes" %}
                            <p>{{ emp_doc.custom_esic_account_number or "" }}</p>
                        {% else %}
                            <p>Not Applicable</p>
                        {% endif %}




                    </div>
                </div>       
            </div>
        </td>
    </tr>

    <tr>
        <td>
            <div class="split-content">
                <div class="first-part">
                    <p><b>Earnings</b></p>
                </div>
                <div class="second-part">
                    <p><b>Master</b></p>
                </div>
                <div class="third-part">
                    <p><b>Actual</b></p>
                </div>
            </div>
        </td>
        <td>
            <div class="split-content">
                <div class="firstpart">
                    <p><b>Deductions</b></p>
                </div>
                <div class="secondpart">
                    <p><b>Actual</b></p>
                </div>
            </div>
        </td>
    </tr>
    <tr>
        <td>
            {% for row in doc.earnings %}
                <div class="split-content">
                    <div class="first-part">
                        <p>{{ row.salary_component }}</p>
                    </div>
                    <div class="second-part">
                       {% if row.amount == row.amount | int %}
                            {{ row.amount | int }}
                        {% else %}
                            {{ row.amount }}
                        {% endif %}
                    </div>
                    <div class="third-part">
                        {% if row.amount == row.amount | int %}
                            {{ row.amount | int }}
                        {% else %}
                            {{ row.amount }}
                        {% endif %}
                    </div>
                </div>
            {% endfor %}
        </td>
        <td>
            {% for row in doc.deductions %}
                <div class="split-content">
                    <div class="firstpart">
                        <p>{{ row.salary_component }}</p>
                    </div>
                    <div class="secondpart">
                        {% if row.amount == row.amount | int %}
                            {{ row.amount | int }}
                        {% else %}
                            {{ row.amount }}
                        {% endif %}
                    </div>
                </div>
            {% endfor %}
        </td>
    </tr>
    <tr>
        <td>
            <div class="split-content">
                <div class="first-part">
                    <p>Total Earnings:{{doc.currency}}.</p>
                </div>
                <div class="second-part">
                    {% if doc.gross_pay == doc.gross_pay | int %}
                        {{ doc.gross_pay | int }}
                    {% else %}
                        {{ doc.gross_pay }}
                    {% endif %}
                </div>
                <div class="third-part">
                    {% if doc.gross_pay == doc.gross_pay | int %}
                        {{ doc.gross_pay | int }}
                    {% else %}
                        {{ doc.gross_pay }}
                    {% endif %}
                </div>
            </div>
        </td>
        <td>
        <div class="split-content">
                <div class="firstpart">
                    <p>Total Deductions:{{doc.currency}}.</p>
                </div>
                <div class="secondpart">
                    {% if doc.total_deduction == doc.total_deduction | int %}
                        {{ doc.total_deduction | int }}
                    {% else %}
                        {{ doc.total_deduction }}
                    {% endif %}
                </div>
            </div></td>
    </tr>
    <tr>
        <td class="amount" colspan="2">
            <p>Net Pay for the month ( Total Earnings - Total Deductions): <b>
                {% if doc.rounded_total == doc.rounded_total | int %}
                    {{ doc.rounded_total | int }}
                {% else %}
                    {{ doc.rounded_total }}
                {% endif %}</b></p>
            <p>({{doc.total_in_words}})</p>
        </td>
    </tr>
</table>

<div>
    {% if letter_head and not no_letterhead -%}
    {% if print_settings.repeat_header_footer %}
    <div class="letter-head-footer divFooter bg-footer" > {{ footer }}</div> 
    {%- endif %}{%- endif %}          
</div>
